});
cy.log(`Verify the file presence based on the count logic in the 'downloads' directory by comparing before file download vs after file download`).task('dynamicFiledownload', 'cypress/downloads').then(before => {
  cy.contains('.txt').click();
  cy.task('dynamicFiledownload', 'cypress/downloads').then(after => {
    expect(after.length).to.be.eq(before.length + 1);
    const downloadedFileName = after.filter(file => !before.includes(file))[0];
    expect(downloadedFileName).includes('.txt');
  });
});

